mapper.select:
select
supplier.supplier_name,
mfrs.mfrs_name,
customers.customer_name,
item.id,
item.item_code,
item.item_no,
item.item_name,
item.product_use_methods,
item.item_short_name,
item.item_specification,
item.item_type,
item.item_own,
item.category_type,
item.customer_item_code,
item.m_item_type,
item.s_item_type,
item.colour_number,
item.appearance,
item.specifications,
item.unit,
item.wh_stock_unit,
item.discount,
item.brand,
item.brand_own,
item.price,
item.tare_weight,
item.gross_weight,
item.net_weight,
item.line_net_weight,
item.identification_net_weight,
item.filling_volume_upper,
item.filling_volume_lower,
item.filling_unit,
item.image_url,
item.density,
item.image_url2,
item.inner_image_url,
item.inner_image_url2,
item.description,
item.net_content,
item.percentage,
item.bar_code,
item.customer_id,
item.property,
item.supplier_id,
item.manufacturer_id,
item.note,
item.warn_toplimit,
item.warn_lowerlimit,
item.content,
item.storage_condition,
item.place_origin,
item.Introduction_conditions,
item.Introduction_date,
item.effective_Day,
item.need_quality,
item.need_barcode_stockout,
item.need_weigh,
item.need_confidential,
item.vulgo,
item.melting_point,
item.particle_size,
item.conventional_packing,
item.submission_code,
item.reach_no,
item.origin,
item.first_aid,
item.status,
item.create_by,
item.create_time,
item.update_by,
item.update_time,
item.product_type,
item.submission_code_sts
from base_item item LEFT JOIN base_supplier supplier on item.supplier_id = supplier.id
LEFT JOIN base_mfrs mfrs on item.manufacturer_id = mfrs.id
LEFT JOIN base_customers customers on item.customer_id = customers.id

    and item.supplier_id = #{supplierId}
    and item.item_code like concat('%', #{itemCode}, '%')
    and item.item_no = #{itemNo}
    and item.item_name like concat('%', #{itemName}, '%')
    and item.product_use_methods =
        #{productUseMethods}

    and item.item_short_name like concat('%',
        #{itemShortName}, '%')

    and item.item_specification =
        #{itemSpecification}

    and item.item_type = #{itemType}
    and item.item_own = #{itemOwn}
    and item.category_type = #{categoryType}
    and item.customer_item_code =
        #{customerItemCode}

    and item.m_item_type = #{mItemType}
    and item.s_item_type = #{sItemType}
    and item.colour_number = #{colourNumber}
    and item.appearance = #{appearance}
    and item.specifications = #{specifications}
    and item.unit = #{unit}
    and item.wh_stock_unit = #{whStockUnit}
    and item.discount = #{discount}
    and item.brand = #{brand}
    and item.brand_own = #{brandOwn}
    and item.price = #{price}
    and item.line_net_weight = #{lineNetWeight}
    and
        item.identification_net_weight
        = #{identificationNetWeight}

    and item.filling_volume_upper =
        #{fillingVolumeUpper}

    and item.filling_volume_lower =
        #{fillingVolumeLower}

    and item.filling_unit = #{fillingUnit}
    and item.image_url = #{imageUrl}
    and item.density = #{density}
    and item.percentage = #{percentage}
    and item.bar_code = #{barCode}
    and item.customer_id = #{customerId}
    and item.property = #{property}
    and item.manufacturer_id = #{manufacturerId}
    and item.note = #{note}
    and item.warn_toplimit = #{warnToplimit}
    and item.warn_lowerlimit = #{warnLowerlimit}
    and item.content = #{content}
    and item.storage_condition =
        #{storageCondition}

    and item.place_origin = #{placeOrigin}
    and item.Introduction_conditions
        =
        #{introductionConditions}

    and item.Introduction_date = #{introductionDate}
    and item.effective_Day = #{effectiveDay}
    and item.need_quality = #{needQuality}
    and item.need_barcode_stockout = #{needBarcodeStockout}
    and item.need_weigh = #{needWeigh}
    and item.need_confidential = #{needConfidential}
    and item.vulgo = #{vulgo}
    and item.melting_point = #{meltingPoint}
    and item.particle_size = #{particleSize}
    and item.conventional_packing =
        #{conventionalPacking}

    and item.submission_code = #{submissionCode}

    and item.reach_no = #{reachNo}
    and item.origin = #{origin}
    and item.firstAid = #{firstAid}
    and item.status = #{status}
    and item.product_type = #{productType}
    and item.check_status = #{checkStatus}

order by item.item_code DESC

select id, item_code, item_no, item_type, case when need_confidential = '2' then '******' ELSE item_name END as item_name, specifications, unit, wh_stock_unit, status from base_item
#{itemType}
AND status = 2 limit #{limit}

select a.*,b.id as mfrsId,b.mfrs_name from base_item a left join base_mfrs b on a.manufacturer_id = b.id
where a.item_code = #{itemCode}
and a.item_type = #{type}

select bi.* from base_item bi
where bi.item_code = #{itemCode}
and bi.item_name= #{itemName}
and bi.manufacturer_id=(select bc.id from
    base_customers bc where bc.customer_code=#{manufacturerCode})


where
item_type = #{itemType}
and status = 2

select t.id as item_id, t.item_code,t.item_name, t.item_type, IFNULL(vus.usage_stock_qty,0) as usage_stock_qty, IFNULL(pp.avg_price,0) as avg_price, IFNULL(pp.last_price,0) as last_price, IFNULL(gppur.purchase_in_qty,0) as purchase_in_qty from base_item t LEFT JOIN v_usage_stock vus on vus.item_id = t.id and vus.keep_type = #{keepType} LEFT JOIN purchase_price pp on pp.item_id = t.id and pp.`status` = #{status} LEFT JOIN (select pur.item_id, IFNULL(sum(pur.purchase_qty - (pur.deliver_qty + pur.count_qty + pur.good_qty + pur.stockin_good_qty + pur.sample_qty)),0) as purchase_in_qty from purchasedtl pur where pur.item_id in
#{itemId}
and pur.status in(1,2)) gppur on gppur.item_id = t.id where t.id in
#{itemId}
